import React from 'react';
import './TweetCard.css';

function TweetCard({ username, tweet, sentiment, reply }) {
  return (
    <div className="card">
      <h4>{username}</h4>
      <p><strong>Tweet:</strong> {tweet}</p>
      <p><strong>Sentiment:</strong> {sentiment > 0 ? '😊 Positive' : sentiment < 0 ? '😠 Negative' : '😐 Neutral'}</p>
      <p><strong>AI Reply:</strong> {reply}</p>
    </div>
  );
}

export default TweetCard;
