import React, { useEffect, useState } from 'react';
import TweetCard from './TweetCard';

function App() {
  const [tweets, setTweets] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch tweets and analysis
    fetch('http://localhost:3001/api/analyze')
      .then(res => res.json())
      .then(data => {
        setTweets(data);
        setLoading(false);
        
        // After getting tweets, fetch analytics
        return fetch('http://localhost:3001/api/analytics');
      })
      .then(res => res.json())
      .then(data => setAnalytics(data))
      .catch(err => {
        console.error('Error fetching data:', err);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>AI Social Bot ‚Äì Reply Analyzer</h2>
      
      {/* Analytics Dashboard */}
      {analytics && (
        <div className="card" style={{ backgroundColor: '#f8f9fa', marginBottom: '2rem' }}>
          <h3>Dashboard Analytics</h3>
          <div style={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap' }}>
            <div style={{ minWidth: '150px', margin: '10px' }}>
              <p><strong>Total Tweets:</strong> {analytics.totalTweets}</p>
              <p><strong>Response Time:</strong> {analytics.responseTime.toFixed(2)}s</p>
            </div>
            <div style={{ minWidth: '150px', margin: '10px' }}>
              <p><strong>Sentiment Breakdown:</strong></p>
              <p>üòä Positive: {analytics.positiveTweets}</p>
              <p>üòê Neutral: {analytics.neutralTweets}</p>
              <p>üò† Negative: {analytics.negativeTweets}</p>
            </div>
          </div>
        </div>
      )}
      
      {/* Tweets Display */}
      {loading ? (
        <p>Loading tweets and generating AI responses...</p>
      ) : (
        tweets.map((t, i) => <TweetCard key={i} {...t} />)
      )}
    </div>
  );
}

export default App;
